Scripts.OnShow:
if ( not this.selected ) then
this.type = "raids";
if ( getn(CT_RaidTracker_RaidLog) > 0 ) then
this.selected = 1;
end
end
CT_RaidTracker_Update();
CT_RaidTracker_UpdateView();

if(this.type == "item") then
	this.itemplayer = CT_RaidTracker_RaidLog[this.id]["Loot"][this.typeid]["player"]
	this.itemitemid = CT_RaidTracker_RaidLog[this.id]["Loot"][this.typeid]["item"]["id"]
	this.itemtime = CT_RaidTracker_RaidLog[this.id]["Loot"][this.typeid]["time"]
end
